template.value:
import {Action, AnyAction} from "redux"

export const $NAME$Actions = {

    fetched($ARGUMENT_NAME$: Array<any>): AnyAction {
        return {
            type: $FETCHED_ACTION_TYPE$,
            payload: $ARGUMENT_NAME$
        }
    },

    fetchInited(): Action {
        return {
            type: $FETCH_INIT_ACTION_TYPE$
        }
    },

    fetch(filters?: any): VoidThunkAction {
        return (dispatch, getState, {axios}) => {
            dispatch(this.fetchInited());
            axios.get(config.geodata).then(res => {
                dispatch(this.fetched(res.data.content))
            })
        }
    }

};
