template.value:
import {Action, AnyAction} from "redux"
import {ThunkAction} from "redux-thunk"

export const $NAME$Actions = {

    fetched($ARGUMENT_NAME$: Array<any>): AnyAction {
        return {
            type: $FETCHED_ACTION_TYPE$,
            payload: $ARGUMENT_NAME$
        }
    },

    fetchInited(): Action {
        return {
            type: $FETCH_INIT_ACTION_TYPE$
        }
    },

    fetch(filters?: any): $FETCH_RETURN_TYPE$ {
        return async (dispatch, getState, extraArguments) => {
            dispatch(this.fetchInited());
            const res = await fetch("http://httpbin.org/ip");
            const json = await res.json();
            dispatch(this.fetched(json))
        }
    }

};
